import { color } from 'console-log-colors';
import express from 'express';
import initDatabase from './database';
import logger from './middlewares/logger';
import { initRoutes } from './routers';
import cors from 'cors';
import { corsConfig } from './config';
import { initSwagger } from './swagger';
// App settings
const app = express();

app.use(
    cors({
        origin: (origin, cb) => {
            if (corsConfig.origins.includes(origin)) {
                cb(null, { origin: true });
            } else {
                cb(null, { origin: false });
            }
        },
    })
);

initSwagger(app);

// Before middlewares
app.use(logger);

// Init database
initDatabase();

// Init routes
app.use('/', initRoutes());

// Init server
app.listen(process.env.PORT, () => {
    try {
        console.log(
            color.blue('Listening on port :: '),
            color.yellow(process.env.PORT)
        );
    } catch (e) {
        console.log(color.red('Error init server :: '), process.env.PORT);
    }
});

export default app;

import { color } from 'console-log-colors';
import mongoose from 'mongoose';
import { dbConfig } from '../config';

// Set Mongo Connection URI, use process.env.NODE_ENV to change config
const dbUri: string =
    'mongodb://' + dbConfig.host + ':' + dbConfig.port + '/' + dbConfig.name;

const initDatabase = async () => {
    try {
        return await mongoose.connect(dbUri, (err) => {
            if (err) {
                console.log(
                    color.red('Error connect database :: '),
                    color.yellow(err)
                );
            } else {
                console.log(
                    color.blue('Database connected to :: '),
                    color.yellow(dbUri)
                );
            }
        });
    } catch (e) {
        return console.log(e);
    }
};

export default initDatabase;

import dotenv from 'dotenv';

dotenv.config();

