    origins: process.env.ORIGINS.split(','),
}

import express from 'express';
import moment from 'moment-timezone';
import { color, log } from 'console-log-colors';

const consoleTrack = (req: any, res: any, next: any) => {
    let startString: string;

    const now: any = moment().utc();

    const startTime: string = now.format('DD/MM/YY hh:mm:ss');

    const method: string = req.method;
    const origin: string = req.get('host');
    const route: string = req.url;

    res.on('finish', () => {
        let statusCode: string;
        let timing: string = '';

        const responseTime: number = moment().utc().diff(now);

        if (responseTime < 400) {
            timing = color.green(responseTime + ' ms ');
        } else if (responseTime >= 400 && responseTime <= 1000) {
            timing = color.yellow(responseTime + ' ms ');
        } else if (responseTime >= 1000) {
            timing = color.red(responseTime + ' ms ');
        }

        if (res?.data?.error || res.statusCode >= 400) {
            startString = color.red('ERROR :: ');
            statusCode = color.red(res.statusCode + ' ');
        } else {
            startString = color.green('SUCCESS :: ');
            statusCode = color.green(res.statusCode + ' ');
        }

        console.log(
            startString +
                ' From: ' +
                color.yellow('"') +
                origin +
                ' - ' +
                startTime +
                color.yellow('"') +
                ' To: ' +
                color.yellow('"') +
                method +
                ': ' +
                route +
                color.yellow('"') +
                ' Status Code: ' +
                statusCode +
                ' Response Time: ' +
                timing
        );
    });

    next();
};

export default consoleTrack;

import { Router } from 'express';
import TRMRouter from './trms';

export const initRoutes = (): Router => {
    const app: Router = Router();

    TRMRouter(app);

    return app;
};

import { Router, Request, Response } from 'express';
import axios from 'axios';
import { exchangeConfig } from '../config';
import { Error } from '../helpers/Error';
import trmControllers from '../controllers/trm';
import { TRM } from '../types/TRM';
import _ from 'lodash';
const router = Router();

const TRMRouter = (app: Router) => {
    app.use('/trm', router);

    const trmController = new trmControllers();

    /**
     * @swagger
     * /trm/:
     *   get:
     *     summary: Get the latest exchange rate between provided currencies
     *     description: Pass source & target currencies codes into query
     *     tags:
     *       - TRM
     *     parameters:
     *       - target:
     *         name: target
     *         type: string
     *         in: query
     *         required: true
     *         description: Currency string code target
     *       - source:
     *         name: source
     *         type: string
     *         in: query
     *         required: true
     *         description: Currency string code source
     *     responses:
     *       '200':
     *         description: Currency rate getted
     *       '400':
     *         description: No data provided in request query
     *       '404':
     *         description: No data found
     *       '500':
     *         description: Unexpected or database error
     */
    router.get('/', async (req: Request, res: Response, next) => {
        const { source, target } = req.query;

        // Verifying data
        if (!source || !target) {
            Error(
                'Insert exchange data (source & target)',
                'EXCHANGE_NO_DATA_PROVIDED',
                res
            );
            return next();
        }

        // Call to ExchangeRates API
        try {
            const apiResponse: any = await axios.get(
                exchangeConfig.baseUrl +
                    exchangeConfig.apiKey +
                    '/pair/' +
                    source +
                    '/' +
                    target,
                {
                    headers: {
                        Authorization: 'Bearer ',
                    },
                }
            );

            // Verifying data from response
            if (!apiResponse || !apiResponse.data) {
                Error('No data', 'EXCHANGE_NO_DATA_FOUND', res);
                return next();
            }

            const { data }: any = apiResponse;

            if (!data.result || data.result !== 'success') {
                Error('Failed Response', 'EXCHANGE_REQUEST_FAILED', res);
                return next();
            }

            // Insert data into DB
            try {
                // Parse data format, this is necessary 'cause transferwise is broken
                let newTRM: TRM = {
                    source: data.base_code,
                    target: data.target_code,
                    rate: data.conversion_rate,
                    time: data.time_last_update_utc,
                };
                const inserted: any = await trmController.create(newTRM);

                res.status(200).json({ result: 'success', data: inserted });
                return next();
            } catch (e) {
                Error(e, 'DB_ERROR_INSERTING', res);
                return next();
            }
        } catch (e) {
            Error(e, 'EXCHANGE_REQUEST_FAILED', res);
            return next();
        }
    });

    /**
     * @swagger
     * /trm/list:
     *   get:
     *     summary: Get the latest exchange rate between provided currencies
     *     description: Pass from and limit numbers into query to set pagination
     *     tags:
     *       - TRM
     *     parameters:
     *       - target:
     *         name: target
     *         type: string
     *         in: query
     *         required: false
     *         description: Currency string code target
     *       - source:
     *         name: source
     *         type: string
     *         in: query
     *         required: false
     *         description: Currency string code source
     *       - limit:
     *         name: limit
     *         type: number
     *         in: query
     *         required: true
     *         description: Limit to pagination
     *       - from:
     *         name: from
     *         type: number
     *         in: query
     *         required: true
     *         description: Index from pagination starts
     *     responses:
     *       '200':
     *         description: Paginated list of currencies
     *       '400':
     *         description: No data provided in request query
     *       '404':
     *         description: No data found
     *       '408':
     *         description: Bad query format
     *       '500':
     *         description: Unexpected or database error
     */
    router.get('/list', async (req: Request, res: Response, next) => {
        // Get query params
        const { from, limit, target, source } = req.query;

        // If no required data was provided
        if (!from || !limit) {
            Error(
                'No pagination data found (from & limit)',
                'LIST_NO_DATA_PROVIDED',
                res
            );
            return next();
        }

        const limitNumber: number = parseInt(limit as string);
        const fromNumber: number = parseInt(from as string);

        if (isNaN(limitNumber) || isNaN(fromNumber)) {
            Error(
                'Bad format pagination data (from & limit)',
                'LIST_WRONG_DATA',
                res
            );
            return next();
        }
        // Get list
        try {
            let query: any = {};
            // If target & source was provided
            if (target && source) {
                query = {
                    target,
                    source,
                };
            }

            // Make query to DB
            const data: Array<TRM> = await trmController.findMany(
                { ...query },
                { skip: fromNumber, limit: limitNumber }
            );

            // If no data was returned
            if (!data) {
                Error('No data found', 'LIST_NO_DATA_FOUND', res);
                return next();
            }

            // Response
            res.status(200).json({ result: 'success', data });
            return next();
        } catch (e) {
            // Error handler
            Error('No data found', 'DB_ERROR_GETTING', res);
            return next();
        }
    });
};

export default TRMRouter;

import { color } from 'console-log-colors';
import { enviroment } from '../config';
const errors = require('../errors/index.json');

export const Error = (err: string, code: string, res: any) => {
    let errorData: any;

    if (errors[code]) {
        errorData = errors[code];
    } else {
        errorData = errors['UNEXPECTED_ERROR'];
    }

    let error: string =
        color.red('ERROR :: ') + color.yellow(errorData.message);

    console.log(error);
    if (enviroment.env === 'development') {
        console.log(err);
    }

    res.status(errorData.status).json(errorData);
};

import { Model } from 'mongoose';
import trmModel from '../models/TRM';
import _ from 'lodash';
import { TRM } from '../types/TRM';

export default class trmControllers {
    private trmModel: Model<TRM>;

    constructor() {
        this.trmModel = trmModel;
    }

    public get model(): Model<TRM> {
        return this.trmModel;
    }

    public async create(data: any): Promise<TRM> {
        /**Custom parsing here */
        const newTRM = await this.trmModel.create(data);

        return newTRM;
    }

    public async findMany(findData: any, parameters: any): Promise<TRM[]> {
        const trms: Array<TRM> = await this.trmModel.find(
            { ...findData },
            {},
            { ...parameters }
        );

        return trms;
    }
}

import mongoose from 'mongoose';
import { TRM } from '../types/TRM';
const { Schema } = mongoose;

const trmModel = new Schema<TRM>({
    source: {
        type: String,
        required: true,
    },
    target: {
        type: String,
        required: true,
    },
    rate: {
        type: Number,
        required: true,
    },
    time: {
        type: Date,
        required: true,
    },
});

export default mongoose.model<TRM>('TRM', trmModel);

import swaggerUi from 'swagger-ui-express';
import swaggerJsDoc from 'swagger-jsdoc';
import { color } from 'console-log-colors';

export const initSwagger = (app: any): any => {
    const config = {
        swaggerDefinition: {
            openapi: '3.0.0',
            info: {
                title: 'Test Express API with Swagger',
                version: '0.1.0',
                description:
                    'This is a simple CRUD API application made with Express and documented with Swagger',
                contact: {
                    name: 'Angel Martinez',
                    url: 'https://amartinez.web.app',
                    email: 'angelmartinezwb@gmail.com',
                    servers: { url: 'http://localhost:8000' },
                },
            },
            servers: [
                {
                    url: 'http://localhost:8000',
                },
            ],
        },
        apis: ['src/routers/*.ts'],
    };

    try {
        const specs = swaggerJsDoc(config);

        app.use('/docs', swaggerUi.serve, swaggerUi.setup(specs));

        console.log(color.blue('Swagger initialized'));
    } catch (e) {
        console.log(color.red('Error initializing Swagger'));
    }

    return app;
};
